mapper.update:
UPDATE product_buy SET 
ps_no= 2
WHERE pb_no=#{pb_no}

UPDATE product_io SET 
ps_no= 2
WHERE io_no=#{io_no}

mapper.select:
SELECT * FROM  product_io AS PI 
JOIN book ON PI.b_no = book.b_no
JOIN processing_status PS ON PI.ps_no = PS.ps_no AND PI.pb_no = #{pb_no}
WHERE PI .ps_no = 10;
